(function($){$.fn.scrollex=function(opts){var settings=$.extend({top:0,bottom:0,delay:0,mode:'default',enter:null,leave:null,initialize:null,terminate:null,scroll:null},opts);return this.each(function(){var t=$(this),state=null,id;id=setInterval(function(){var a=window.scrollY,tH=window.innerHeight,b=a+tH,e=t.offset().top,o=t.outerHeight(),top=b-settings.top,bottom=a+settings.bottom;var visible=(o>0&&(b>e)&&(bottom<e+o));if(settings.scroll)settings.scroll();if(visible){if(state!='visible'){state='visible';if(settings.enter)settings.enter();}}else{if(state!='hidden'){state='hidden';if(settings.leave)settings.leave();}}},settings.delay);});};})(jQuery);
